import React, { useState, useEffect } from "react";
import RecordCard from "./RecordCard";


function Collection({recordsInCollection, setRecordsInCollection, user, collectors}) {

    document.body.style.backgroundImage = "url('https://static.vecteezy.com/system/resources/previews/006/797/720/original/retro-background-groovy-background-free-vector.jpg')"
    document.body.style.backgroundRepeat = "repeat"
    document.body.style.backgroundSize = 'auto'

    const [userOrders, setUserOrders] = useState([])

    const handleDeleteFromCollectionClick = (record) => {
        const filterRecordsInCollection = recordsInCollection.filter(recordInCollection => recordInCollection.id !== guitar.id)
        setRecordsInCollection(filterRecordsInCollection)
    }

    const renderRecordsInCollection = recordsInCollection.map(record =>
        <RecordCard record={record} isInCollection={true} key={record.id} handleDeleteFromCollectionClick={handleDeleteFromCollectionClick} placeOrder={placeOrder}
        />)


    const renderUserOrders = userOrders.map(order => 
        <OrderCard order={order} key={order.order_number}/>)


    const isValid = Boolean(guitarsInCart.length > 0 && user.length > 0)

    return (
        <div className="cart-container">
            <div className="guitar-cart-container">
                <div className="cart-headers">
                    <h2>Total: ${cartTotal}</h2>
                    <button id="order-btn" onClick={placeOrder} disabled={!isValid}>{isValid ? "Place order" : "Please log in & add guitars"}
                    </button>
                </div>
                    <h1>Cart Items</h1>
                <div className="guitars-in-cart">
                    {renderGuitarsInCart}
                </div>
            </div>
            <div className="order-container">
                <h1>Order History</h1>
                <div className="overflow-container">
                    {renderUserOrders}
                </div>
            </div>
        </div>
    )
}

export default Collection;
