import React from "react";

function Search({setSearch}){

  function handleSearch(e){
    let value= e.target.value
    setSearch(value)
  }
  return (
     <div id="input-container"> 
      <input onChange={handleSearch}
        type="text"
        id="search"
        placeholder="Lookup Record"
      />
      </div>

    );
}
export default Search
